import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
   let [Notes,setNote]=React.useState([{
    id:new Date().getTime(),
    title:"First",
    content:"Description....."}] );
    function addNote(props){
        let newNote={
            id:props.id,
            title:props.title,
            content:props.content
        }
        setNote(prevNote=>{
            return [...prevNote,newNote];
        });
    } 
//render all the notes
function renderNOtes(props,index){
return <Note key={index} title={props.title} content={props.content}/>

}

  return (
    <div>
      <Header />
      <CreateArea addNote={addNote}/>
      {Notes.map(renderNOtes)}
      <Footer />
    </div>
  );
}

export default App;
