import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
   let [Notes,setNote]=React.useState([{
    id:new Date().getTime(),
    title:"First",
    content:"Description....."}] );
    function addNote(props){
        let newNote={
            id:props.id,
            title:props.title,
            content:props.content
        }
        setNote(prevNote=>{
            return [...prevNote,newNote];
        });
    } 
//render all the notes
function renderNOtes(props,index){
return <Note onDelete={onDelete}key={index} id={props.id} title={props.title} content={props.content}/>

}
//the delte function
function onDelete(id){
setNote(prevNote=>{
  Notes.filter(note=>{
    if(Notes.length==1){
      return {id:0,title:"",content:""};
    }
    return note.id!==id;
  });
})
}
  return (
    <div>
      <Header />
      <CreateArea addNote={addNote}/>
      {Notes.map(renderNOtes)}
      <Footer />
    </div>
  );
}

export default App;
