import React from "react";
//CreateArea.jsx
function CreateArea(props) {
  let [note,setNote]=React.useState({ id: 0,title:" ",content:" "});
  function handleChange(event){
      
    let {name,value}=event.target;
     
     
    setNote(prevNote => {
    return {
      ...prevNote,
      [name]: value,
       
    };
  });
  }
  function handleClick(event){
   props.addNote(note);
   setNote({id:0,title:" ",content:" "});
   event.preventDefault();
  }
  return (
    
    <div>
      <form>
        <input onChange={handleChange} value type="text" name="title" placeholder="Title" />
        <textarea onChange={handleChange} type="text"name="content" placeholder="Take a note..." rows="3" />
        <button onClick={handleClick}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
