import React from "react";
//CreateArea.jsx
function CreateArea(props) {
  let [note,setNote]=React.useState({title:" ",content:" "});
  function handleChange(event){
     console.log(event.target);
    let {name,value}=event.target;
    let newnote={
      title:"",
      content:""
     }
     
    setNote(prevnote=>{ 
     if (name === "title") {
      return { ...prevnote, title: value };
    } else {
      return { ...prevNote, content: value };
    }});
  }
  function handleClick(){
   props.addNote(note);
  }
  return (
    
    <div>
      <form>
        <input onChange={handleChange} type="text" name="title" placeholder="Title" />
        <textarea onChange={handleChange} type="text"name="content" placeholder="Take a note..." rows="3" />
        <button onClick={handleClick}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
